//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "./GameBase.sol";

/**

    [Board]

    2 bits * 9 spaces = 18 bits

    00 00 00 00 00 00 00 00 00

    [Board Indices]

    08 07 06
    05 04 03
    02 01 00

    [Board Spaces]

    2 bits each

    Bits  Hex  #  Type
    ----  ---  -  ----
    00    0x0  0  Empty
    01    0x1  1  Player 1 (Circle)
    10    0x2  2  Player 2 (Cross)

    [Turn Count - 4 bits]

    0000-1000   (Turns 0-8)

    [Turn Indicator - 1 bit]

    Bit   Hex  # Type
    ----  ---  - ----
    0     0x0  0 Player 1
    1     0x1  1 Player 2

    [Winner - 1 bit]

    Bit   Hex  # Type
    ----  ---  - ----
    0     0x0  0 Player 1
    1     0x1  1 Player 2

    [Data Variable - 24 bits]

                    [Winner] [Turn]  [Count]  [Board]
                    0        0       0000     00 00 00 00 00 00 00 00 00
                    ----------------------------------------------------
        Board Index                           8  7  6  5  4  3  2  1  0
                    ----------------------------------------------------

    */

contract TicTacToe is GameBase {
    /** Constants **/
    uint8 public constant EMPTY = 0x0;
    uint8 public constant PLAYER_1 = 0x1;
    uint8 public constant PLAYER_2 = 0x2;
    enum GameSquareState {
        EMPTY,
        PLAYER_1,
        PLAYER_2
    }

    uint8 public constant TURN_COUNT_OFFSET = 0x12;
    uint8 public constant CURRENT_PLAYER_OFFSET = 0x16;
    uint8 public constant GAME_WINNER_OFFSET = 0x17;

    uint8 public constant TURN_COUNT_SLICE_SIZE = 0xF;
    uint8 public constant CURRENT_PLAYER_SLICE_SIZE = 0x1;
    uint8 public constant GAME_WINNER_SLICE_SIZE = 0x1;

    /** Structs **/
    struct GameData {
        uint256 gameId;
        uint256 timestamp;
        address playerOne;
        address playerTwo;
        uint24 state;
        bool canJoin;
        bool isStarted;
    }

    // /** Properties **/
    // CountersUpgradeable.Counter public gamesPlayed;

    // mapping(uint256 => GameData) public gameData;
    // uint256[] games;

    // /** Events **/
    // event GameCreated(uint256 gameId, address player);
    // event PlayerJoined(uint256 gameId, address player);
    // event PlayerQuit(uint256 gameId, address player);
    // event PlayerMoved(uint256 gameId, uint8 index);
    // event GameOver(uint256 gameId, address winner);

    // modifier checkGameExists(uint256 gameId) {
    //     require(gameId <= gamesPlayed.current(), "Game does not exist");
    //     _;
    // }

    // modifier checkGameJoinable(uint256 gameId) {
    //     require(gameData[gameId].canJoin, "Game is full! Can't join");
    //     _;
    // }

    modifier isValidMove(uint24 _board, uint8 index) {
        require(getSpace(_board, index) == 0, "Space is already occupied");
        _;
    }

    function initialize() public {}

    /** Game Management **/

    /** Game Data **/
    function getDataItem(
        uint24 gameState,
        uint8 bitOffset,
        uint8 sliceSize
    ) public pure returns (uint24) {
        return ((gameState >> bitOffset) & sliceSize);
    }

    function getSpace(uint24 _board, uint8 index) internal pure returns (uint24) {
        return ((_board >> (index * 2)) & 0x3);
    }

    /** Gameplay Methods **/
    function applyMove(
        GameData memory _data,
        uint8 index,
        uint8 player
    ) internal pure isValidMove(_data.state, index) {
        uint24 move = player << (index * 2);

        _data.state |= move;

        // todo: increment turn counter

        // todo: check for win states

        // emit PlayerMoved(_data.gameId, index);
    }

    /**

    -- Reversing Indexes --

    Actual  4  3  2  1  0

            FF 01 AA 03 77

    Mapped  0  1  2  3  4

    -(x + 1)

    -((spaces - 1) + 1) = -(4 + 1) = -5

    (mappedIndex - (spaces - 1)) * 1 = actualIndex

    4 - 4 = 0 = 0

    3 - 4 = -1 = 1

    2 - 4 = -2 = 2

    1 - 4 = -3 = 3

    0 - 4 = -4 = 4


        Intended    Actual  
    0   FF          03      
    1   01          AA      
    2   AA          01      
    3   03          FF      

    3 - 0 = 3
    3 - 2 = 1
    3 - 1 = 2
    3 - 3 = 0

    Actual -> Intended

    maxIndex - index = reversedIndex

    3 - 2 = 1
    3 - 3 = 0
    3 - 1 = 2

    maxIndex = reversedIndex + index

    [2D] 3x3 Board

    01 01 01
    01 01 01
    01 01 01

    [1D] 9x1 Board (mapping left to right, top to bottom)

    01 01 01 01 01 01 01 01 01

    -- Winning Board States --

    [FF FF FF 01 01 01 01 01 01]
    [01 01 01 FF FF FF 01 01 01]
    [01 01 01 01 01 01 FF FF FF]

    [FF 01 01 FF 01 01 FF 01 01]
    [01 FF 01 01 FF 01 01 FF 01]
    [01 01 FF 01 01 FF 01 01 FF]

    [FF 01 01 01 FF 01 01 01 FF]
    [01 01 FF 01 FF 01 FF 01 01]

    1. Three adjacent marks
    2. Three marks separated by two spaces
    3. Three marks at the beginning, middle, and end of the board
    4. Three marks in the middle of the board separated by one space

    [Horizontal]

    FF FF FF
    01 01 01
    01 01 01

    FF FF FF 01 01 01 01 01 01

    01 01 01
    FF FF FF
    01 01 01

    01 01 01 FF FF FF 01 01 01

    01 01 01
    01 01 01
    FF FF FF

    01 01 01 01 01 01 FF FF FF
`
    [Vertical]

    FF 01 01
    FF 01 01
    FF 01 01

    FF 01 01 FF 01 01 FF 01 01

    01 FF 01
    01 FF 01
    01 FF 01

    01 FF 01 01 FF 01 01 FF 01

    01 01 FF
    01 01 FF
    01 01 FF

    01 01 FF 01 01 FF 01 01 FF

    [Diagonal]

    FF 01 01
    01 FF 01
    01 01 FF

    FF 01 01 01 FF 01 01 01 FF

    01 01 FF
    01 FF 01
    FF 01 01

    01 01 FF 01 FF 01 FF 01 01

     */
